export default function smartTutorAI(text) {
  if (!text || text.trim() === "") {
    return "Bujhte parini—arektu clear kore bolben?";
  }

  const msg = text.toLowerCase();

  // GREETING
  if (
    msg.includes("hello") ||
    msg.includes("hi") ||
    msg.includes("hey") ||
    msg.includes("tutor")
  ) {
    return "Hello! Ami apnar smart tutor. Bolun, ki problem solve korte chai?";
  }

  // WORDPRESS RELATED
  if (msg.includes("wordpress") || msg.includes("elementor") || msg.includes("plugin") || msg.includes("theme")) {
    return "Apni WordPress niye kaj korte chacchen. Please bolun — theme, plugin, error, naki full website setup chai?";
  }

  // WEBSITE RELATED
  if (msg.includes("website") || msg.includes("web site") || msg.includes("site banate chai")) {
    return "Website banate chaile ami help korte pari — WordPress, React, custom code shob e. Ki type website banate chacchen?";
  }

  // ERROR FIXING
  if (
    msg.includes("error") ||
    msg.includes("not working") ||
    msg.includes("problem") ||
    msg.includes("issue") ||
    msg.includes("fix")
  ) {
    return "Error fix korte ami ready. Apnar full error ta bolun — ami step by step fix kore dibo.";
  }

  // AUTOMATION
  if (
    msg.includes("automation") ||
    msg.includes("auto") ||
    msg.includes("workflow") ||
    msg.includes("smythos") ||
    msg.includes("make") ||
    msg.includes("zapier")
  ) {
    return "Automation niye help korte pari. Workflow ta ki korte hobe bolun — ami full setup pathabo.";
  }

  // AI / CHATBOT
  if (msg.includes("ai") || msg.includes("chatbot") || msg.includes("voice")) {
    return "AI/Chatbot setup korte chai? Frontend + backend + voice setup ami guide kore dibo. Ki type bot banate chacchen?";
  }

  // DEFAULT FALLBACK
  return "Bujhte parini—arektu details dile balo moto help korte parbo.";
}
