  }

  const input = text.toLowerCase();

  // GREETING
  if (input.includes("hello tutor") || input.includes("hi tutor")) {
    return "Hello! I am your tutor. Ask any question about your project.";
  }

  /* -------------------------
     AUTOMATION
  -------------------------- */
  if (
    input.includes("automation") ||
    input.includes("automate") ||
    input.includes("workflow") ||
    input.includes("bot") ||
    input.includes("smythos") ||
    input.includes("ai automation")
