  if (input.includes("i want") || input.includes("ami chai") || input.includes("ami cai")) {
    return "Thik ache! Apni ki chan? Aro details dile ami guide kore debo.";
  }

  /* -------------------------
     DEFAULT
  -------------------------- */
  return "Bujhte parini â€” arektu details bolben? Ami step-by-step guide diye solve kore dibo.";
}
