// smartTutorAI.js

export default function smartTutorAI(text) {
  if (!text) {
    return "Bujhte parini — arektu details bolben? (e.g., 'my React state not updating')";
  }

  const input = text.toLowerCase().trim();

  // GREETING
  if (input.includes("hello tutor") || input.includes("hi tutor")) {
    return "Hello! I am your tutor. Ask any question about your project.";
  }

  // WEBSITE TOPIC
  if (input.includes("i want a website") || input.includes("website")) {
    return "Thik ache! Apni ki dhoroner website chan? (Blog, Ecommerce, Business, Portfolio?)";
  }

  // DEFAULT RESPONSE
  return "Bujhte parini — arektu details bolben? (e.g., 'my React state not updating')";
}
