import React, { useState, useEffect, useRef } from "react";
import SpeechRecognition, { useSpeechRecognition } from "react-speech-recognition";

function App() {
  const [messages, setMessages] = useState([]); // chat history
  const [isSupported, setIsSupported] = useState(true);
  const { transcript, listening, resetTranscript } = useSpeechRecognition();
  const chatEndRef = useRef(null);

  // Check if speech recognition is supported
  useEffect(() => {
    if (!SpeechRecognition.browserSupportsSpeechRecognition()) {
      setIsSupported(false);
    }
  }, []);

  // Scroll to bottom on new message
  useEffect(() => {
    if (chatEndRef.current) {
      chatEndRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [messages]);

  const startListening = () => {
    resetTranscript();
    SpeechRecognition.startListening({ continuous: false, language: "en-US" });
  };

  const stopListening = () => {
    SpeechRecognition.stopListening();
  };

  // Handle tutor reply
  const askTutor = (text) => {
    if (!text.trim()) return;

    // Add user message
    setMessages((prev) => [...prev, { sender: "user", text }]);

    // Generate mock tutor reply
    const mockReply = `You said: "${text}". This is your mock tutor speaking!`;

    setTimeout(() => {
      // Add tutor message
      setMessages((prev) => [...prev, { sender: "tutor", text: mockReply }]);

      // Play voice
      const utterance = new SpeechSynthesisUtterance(mockReply);
      utterance.lang = "en-US";
      window.speechSynthesis.speak(utterance);
    }, 500); // small delay for realism
  };

  // Trigger tutor response when transcript captured
  useEffect(() => {
    if (transcript && transcript.trim() !== "") {
      askTutor(transcript);
      resetTranscript();
    }
  }, [transcript]);

  if (!isSupported) return <p>Your browser does not support speech recognition.</p>;

  return (
    <div style={{ padding: "20px", fontFamily: "sans-serif", maxWidth: "600px", margin: "0 auto" }}>
      <h1>üé§ Mock Voice Chat Tutor</h1>
      <p>Speak to chat with your tutor! üéß</p>

      <div style={{ marginBottom: "10px" }}>
        <button onClick={startListening} style={{ marginRight: "10px", padding: "0.5rem 1rem" }}>Start Listening</button>
        <button onClick={stopListening} style={{ padding: "0.5rem 1rem" }}>Stop</button>
      </div>

      <p>Listening: {listening ? "Yes ‚úÖ" : "No ‚ùå"}</p>
      <p><strong>Current transcript:</strong> "{transcript}"</p>

      <div
        style={{
          border: "1px solid #ccc",
          borderRadius: "8px",
          padding: "10px",
          marginTop: "20px",
          height: "400px",
          overflowY: "auto",
          background: "#f9f9f9",
        }}
      >
        {messages.map((msg, idx) => (
          <div
            key={idx}
            style={{
              display: "flex",
              justifyContent: msg.sender === "user" ? "flex-end" : "flex-start",
              marginBottom: "10px",
            }}
          >
            <div
              style={{
                background: msg.sender === "user" ? "#4caf50" : "#2196f3",
                color: "white",
                padding: "8px 12px",
                borderRadius: "16px",
                maxWidth: "70%",
              }}
            >
              {msg.text}
            </div>
          </div>
        ))}
        <div ref={chatEndRef}></div>
      </div>
    </div>
  );
}

export default App;
