import React, { useState, useEffect } from "react";
import smartTutorAI from "./smartTutorAI";

function VideoTutor() {
  const [text, setText] = useState("");
  const [reply, setReply] = useState("");
  const [tutorVideo, setTutorVideo] = useState("/tutor.mp4");

  // ðŸ§  Handle AI reply whenever text changes
  useEffect(() => {
    if (text.trim() === "") return;
    const generatedReply = smartTutorAI(text);
    setReply(generatedReply);
  }, [text]);

  // ðŸŽ¤ Voice recognition (optional)
  const startListening = () => {
    if (!window.SpeechRecognition && !window.webkitSpeechRecognition) {
      alert("Browser does not support Speech Recognition!");
      return;
    }

    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    const recognition = new SpeechRecognition();
    recognition.lang = "en-US";
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;

    recognition.onresult = (event) => {
      const spokenText = event.results[0][0].transcript;
      setText(spokenText);
    };

    recognition.start();
  };

  return (
    <div style={{ padding: "20px", maxWidth: "500px", margin: "auto" }}>
      <h2>ðŸŽ§ Smart Tutor</h2>

      <input
        type="text"
        placeholder="Type your question..."
        value={text}
        onChange={(e) => setText(e.target.value)}
        style={{ width: "100%", padding: "10px", marginBottom: "10px" }}
      />

      <button onClick={startListening} style={{ padding: "10px", marginBottom: "15px" }}>
        ðŸŽ¤ Start Voice Input
      </button>

      <div style={{ border: "1px solid #ddd", borderRadius: "10px", padding: "15px" }}>
        <p><strong>You said:</strong> {text}</p>
        <p><strong>Tutor:</strong> {reply}</p>
        {tutorVideo && (
          <video src={tutorVideo} width="380" autoPlay loop style={{ borderRadius: "12px" }} />
        )}
      </div>
    </div>
  );
}

export default VideoTutor;
