import React, { useState, useEffect, useRef } from "react";
import SpeechRecognition, { useSpeechRecognition } from "react-speech-recognition";

function App() {
  const [messages, setMessages] = useState([]); // Chat history
  const [isSupported, setIsSupported] = useState(true);
  const { transcript, listening, resetTranscript } = useSpeechRecognition();
  const chatEndRef = useRef(null);

  // Check browser support
  useEffect(() => {
    if (!SpeechRecognition.browserSupportsSpeechRecognition()) {
      setIsSupported(false);
    }
  }, []);

  // Auto-scroll chat
  useEffect(() => {
    if (chatEndRef.current) {
      chatEndRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [messages]);

  const startListening = () => {
    resetTranscript();
    SpeechRecognition.startListening({ continuous: true, language: "en-US" });
  };

  const stopListening = () => {
    SpeechRecognition.stopListening();
  };

  // Generate tutor response (keyword-based logic)
  const generateTutorReply = (text) => {
    const lower = text.toLowerCase();

    if (lower.includes("math")) return "Let‚Äôs solve your math problem step by step!";
    if (lower.includes("react")) return "React is a frontend library used to build user interfaces.";
    if (lower.includes("javascript") || lower.includes("js")) return "JavaScript is a versatile programming language for web development.";
    if (lower.includes("hello") || lower.includes("hi")) return "Hello! How can I help you today?";
    return "Interesting! Can you explain more so I can help?";
  };

  // Handle tutor reply
  const askTutor = (text) => {
    if (!text.trim()) return;

    // Add user message
    setMessages((prev) => [...prev, { sender: "user", text }]);

    // Small delay to simulate thinking
    setTimeout(() => {
      const reply = generateTutorReply(text);

      // Add tutor message
      setMessages((prev) => [...prev, { sender: "tutor", text: reply }]);

      // Speak tutor reply
      const utterance = new SpeechSynthesisUtterance(reply);
      utterance.lang = "en-US";
      window.speechSynthesis.speak(utterance);
    }, 500);
  };

  // Trigger tutor reply after user finishes speaking (debounce)
  useEffect(() => {
    if (transcript && transcript.trim() !== "") {
      const timeout = setTimeout(() => {
        askTutor(transcript);
        resetTranscript();
      }, 1000); // wait 1 second after last word

      return () => clearTimeout(timeout);
    }
  }, [transcript]);

  if (!isSupported) return <p>Your browser does not support speech recognition.</p>;

  return (
    <div style={{ padding: "20px", fontFamily: "sans-serif", maxWidth: "600px", margin: "0 auto" }}>
      <h1>üé§ Interactive Voice Tutor</h1>
      <p>Talk to your tutor and get smart responses! üí¨</p>

      <div style={{ marginBottom: "10px" }}>
        <button onClick={startListening} style={{ marginRight: "10px", padding: "0.5rem 1rem" }}>Start Listening</button>
        <button onClick={stopListening} style={{ padding: "0.5rem 1rem" }}>Stop</button>
      </div>

      <p>Listening: {listening ? "Yes ‚úÖ" : "No ‚ùå"}</p>
      <p><strong>Current transcript:</strong> "{transcript}"</p>

      <div
        style={{
          border: "1px solid #ccc",
          borderRadius: "8px",
          padding: "10px",
          marginTop: "20px",
          height: "400px",
          overflowY: "auto",
          background: "#f9f9f9",
        }}
      >
        {messages.map((msg, idx) => (
          <div
            key={idx}
            style={{
              display: "flex",
              justifyContent: msg.sender === "user" ? "flex-end" : "flex-start",
              marginBottom: "10px",
            }}
          >
            <div
              style={{
                background: msg.sender === "user" ? "#4caf50" : "#2196f3",
                color: "white",
                padding: "8px 12px",
                borderRadius: "16px",
                maxWidth: "70%",
              }}
            >
              {msg.text}
            </div>
          </div>
        ))}
        <div ref={chatEndRef}></div>
      </div>
    </div>
  );
}

export default App;
