// src/api.js
export async function requestTutor(text) {
  try {
    const res = await fetch("http://localhost:5000/api/tutor", {  // <-- full URL added
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ text }),
    });

    if (!res.ok) {
      const errText = await res.text();
      throw new Error(errText || "Server error");
    }

    const data = await res.json();
    console.log("✅ Tutor API Response:", data);
    return data; // { reply, audio, video }
  } catch (error) {
    console.error("❌ Tutor request error:", error);
    return { reply: "Error contacting tutor", audio: "", video: "" };
  }
}
