import React, { useState, useEffect } from "react";

const synth = window.speechSynthesis;

function App() {
  const [listening, setListening] = useState(false);
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [guide, setGuide] = useState([]);

  let recognition;
  if ("webkitSpeechRecognition" in window) {
    recognition = new window.webkitSpeechRecognition();
    recognition.continuous = false;
    recognition.interimResults = false;
    recognition.lang = "en-US";

    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setQuestion(transcript);
      sendQuestion(transcript);
    };
  }

  const startListening = () => {
    setListening(true);
    recognition.start();
  };

  const stopListening = () => {
    setListening(false);
    recognition.stop();
  };

  const sendQuestion = async (q) => {
    const res = await fetch("http://localhost:5000/ask", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ question: q })
    });
    const data = await res.json();
    setAnswer(data.answer);
    setGuide(data.guide);
    speakAnswer(data.answer, data.guide);
  };

  const speakAnswer = (ans, guideSteps) => {
    let utterance = new SpeechSynthesisUtterance(ans + " " + guideSteps.join(". "));
    synth.speak(utterance);
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>AI Tutor (Voice + Guide)</h1>
      <button onClick={listening ? stopListening : startListening}>
        {listening ? "Stop Listening" : "Start Talking"}
      </button>

      {question && <p><b>You asked:</b> {question}</p>}
      {answer && <p><b>Answer:</b> {answer}</p>}
      {guide.length > 0 && (
        <div>
          <b>Step-by-Step Guide:</b>
          <ol>
            {guide.map((g, idx) => <li key={idx}>{g}</li>)}
          </ol>
        </div>
      )}

      {/* Tutor Video Placeholder */}
      <div style={{ marginTop: "20px", border: "2px solid #ccc", width: "400px", height: "300px", textAlign: "center", lineHeight: "300px", fontWeight: "bold" }}>
        Tutor Video Here
      </div>
    </div>
  );
}

export default App;
