import React, { useState, useEffect, useRef } from "react";
import SpeechRecognition, { useSpeechRecognition } from "react-speech-recognition";

function App() {
  const [messages, setMessages] = useState([]);
  const [isSupported, setIsSupported] = useState(true);
  const [darkMode, setDarkMode] = useState(false);
  const [typing, setTyping] = useState(false);
  const [voiceIndex, setVoiceIndex] = useState(0); // Multiple voices
  const { transcript, listening, resetTranscript } = useSpeechRecognition();
  const chatEndRef = useRef(null);

  // Check browser support
  useEffect(() => {
    if (!SpeechRecognition.browserSupportsSpeechRecognition()) {
      setIsSupported(false);
    }
  }, []);

  // Scroll chat to bottom
  useEffect(() => {
    if (chatEndRef.current) {
      chatEndRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [messages]);

  const startListening = () => {
    resetTranscript();
    SpeechRecognition.startListening({ continuous: true, language: "en-US" });
  };

  const stopListening = () => {
    SpeechRecognition.stopListening();
  };

  // Toggle Dark Mode
  const toggleDarkMode = () => setDarkMode(!darkMode);

  // Get available voices
  const getVoices = () => window.speechSynthesis.getVoices();

  // Generate smarter tutor response
  const generateTutorReply = (text) => {
    const lower = text.toLowerCase();
    if (lower.includes("math")) return "Let‚Äôs solve your math problem step by step!";
    if (lower.includes("react")) return "React is a frontend library to build user interfaces.";
    if (lower.includes("javascript") || lower.includes("js")) return "JavaScript is a versatile web programming language.";
    if (lower.includes("hello") || lower.includes("hi")) return "Hello! How can I help you today?";
    if (lower.includes("weather")) return "I can tell you about weather, but check your local forecast for accuracy.";
    return "Interesting! Can you explain more so I can help you better?";
  };

  // Handle tutor reply
  const askTutor = (text) => {
    if (!text.trim()) return;

    // Add user message
    setMessages((prev) => [...prev, { sender: "user", text }]);

    // Show typing effect
    setTyping(true);
    setTimeout(() => {
      const reply = generateTutorReply(text);
      setTyping(false);

      // Add tutor message
      setMessages((prev) => [...prev, { sender: "tutor", text: reply }]);

      // Speak tutor reply
      const utterance = new SpeechSynthesisUtterance(reply);
      const voices = getVoices();
      if (voices.length > 0) utterance.voice = voices[voiceIndex % voices.length];
      utterance.lang = "en-US";
      window.speechSynthesis.speak(utterance);
    }, 1000); // 1 sec typing simulation
  };

  // Trigger tutor reply after user finishes speaking (debounce)
  useEffect(() => {
    if (transcript && transcript.trim() !== "") {
      const timeout = setTimeout(() => {
        askTutor(transcript);
        resetTranscript();
      }, 1000);
      return () => clearTimeout(timeout);
    }
  }, [transcript]);

  if (!isSupported) return <p>Your browser does not support speech recognition.</p>;

  return (
    <div style={{
      padding: "20px",
      fontFamily: "sans-serif",
      maxWidth: "600px",
      margin: "0 auto",
      background: darkMode ? "#121212" : "#f0f0f0",
      color: darkMode ? "#f0f0f0" : "#121212",
      minHeight: "100vh"
    }}>
      <h1>üé§ Polished Interactive Voice Tutor</h1>
      <p>Talk to your tutor and get smart responses with voice! üí¨</p>

      <div style={{ marginBottom: "10px" }}>
        <button onClick={startListening} style={{ marginRight: "10px", padding: "0.5rem 1rem" }}>Start Listening</button>
        <button onClick={stopListening} style={{ padding: "0.5rem 1rem", marginRight: "10px" }}>Stop</button>
        <button onClick={toggleDarkMode} style={{ padding: "0.5rem 1rem" }}>
          {darkMode ? "Light Mode" : "Dark Mode"}
        </button>
        <button
          onClick={() => setVoiceIndex((prev) => prev + 1)}
          style={{ padding: "0.5rem 1rem", marginLeft: "10px" }}
        >
          Change Voice
        </button>
      </div>

      <p>Listening: {listening ? "Yes ‚úÖ" : "No ‚ùå"}</p>
      <p><strong>Current transcript:</strong> "{transcript}"</p>

      <div style={{
        border: "1px solid #ccc",
        borderRadius: "8px",
        padding: "10px",
        marginTop: "20px",
        height: "400px",
        overflowY: "auto",
        background: darkMode ? "#1e1e1e" : "#fff",
      }}>
        {messages.map((msg, idx) => (
          <div key={idx} style={{
            display: "flex",
            justifyContent: msg.sender === "user" ? "flex-end" : "flex-start",
            marginBottom: "10px",
          }}>
            <div style={{
              background: msg.sender === "user" ? "#4caf50" : "#2196f3",
              color: "white",
              padding: "8px 12px",
              borderRadius: "16px",
              maxWidth: "70%",
            }}>
              {msg.text}
            </div>
          </div>
        ))}
        {typing && (
          <div style={{ fontStyle: "italic", marginBottom: "10px" }}>Tutor is typing...</div>
        )}
        <div ref={chatEndRef}></div>
      </div>
    </div>
  );
}

export default App;
