import React, { useState } from "react";

export default function App() {
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");

  const ask = async () => {
    const r = await fetch("http://localhost:5000/ask", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ question })
    });

    const data = await r.json();
    setAnswer(data.answer);
  };

  const listen = () => {
    const rec = new window.webkitSpeechRecognition();
    rec.lang = "bn-BD";
    rec.start();

    rec.onresult = function (e) {
      setQuestion(e.results[0][0].transcript);
    };
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>ðŸŽ¤ Voice Tutor â€“ Offline Guide</h1>

      <button onClick={listen}>ðŸŽ™ Speak</button>

      <textarea 
        value={question}
        onChange={(e) => setQuestion(e.target.value)}
        rows="3"
        cols="50"
      />

      <br/>
      <button onClick={ask}>Ask Tutor</button>

      <h3>ðŸ“˜ Tutor Guide:</h3>
      <pre>{answer}</pre>
    </div>
  );
}
