export default function smartTutorAI(text) {
  if (!text || text.trim().length === 0) {
    return "I couldn’t understand that. Could you please clarify your question?";
  }

  const msg = text.toLowerCase().trim();

  // Professional Greetings
  if (msg.includes("hello") || msg.includes("hi") || msg.includes("hey")) {
    return "Hello! I am your Smart Tutor. How may I assist you today regarding your project or website?";
  }

  // English conversation
  if (msg.includes("english") || msg.includes("talk in english") || msg.includes("speak english")) {
    return "Certainly! I can communicate in English. Please tell me what assistance you need.";
  }

  // WordPress guidance
  if (msg.includes("wordpress") || msg.includes("plugin") || msg.includes("theme")) {
    return "I can guide you through creating or troubleshooting a WordPress website. Could you specify the issue or type of site you want to build?";
  }

  // Website / React
  if (msg.includes("website") || msg.includes("react") || msg.includes("frontend")) {
    return "I can provide step-by-step guidance for building your website or React application. What exactly would you like to accomplish?";
  }

  // API / backend
  if (msg.includes("api") || msg.includes("backend") || msg.includes("server")) {
    return "I can assist with backend or API-related issues. Please describe the problem or the functionality you want to implement.";
  }

  // Voice / AI / Automation
  if (msg.includes("voice") || msg.includes("chatbot") || msg.includes("ai") || msg.includes("automation")) {
    return "I can help you set up voice-enabled AI or automation workflows. Could you provide details about your requirements?";
  }

  // Errors / troubleshooting
  if (msg.includes("error") || msg.includes("not working") || msg.includes("problem")) {
    return "Please provide the exact error message or describe the issue in detail, and I will guide you step-by-step to resolve it.";
  }

  // Generic requests
  if (msg.startsWith("i want") || msg.startsWith("i need") || msg.includes("want") || msg.includes("need")) {
    return "Understood. Could you provide more details so I can offer a professional step-by-step solution?";
  }

  // Fallback professional reply
  return "I am trying to understand your request. Could you please clarify it in more detail?";
}
