import React, { useState, useEffect } from "react";
import SpeechRecognition, { useSpeechRecognition } from "react-speech-recognition";
import "./micTest.js";

function App() {
  const [response, setResponse] = useState("");
  const [audioUrl, setAudioUrl] = useState("");
  const [videoUrl, setVideoUrl] = useState("");
  const [loading, setLoading] = useState(false);
  const [isSupported, setIsSupported] = useState(true);

  const { transcript, listening, resetTranscript } = useSpeechRecognition();

  useEffect(() => {
    if (!SpeechRecognition.browserSupportsSpeechRecognition()) {
      setIsSupported(false);
    }
  }, []);

  const startListening = () => {
    resetTranscript();
    SpeechRecognition.startListening({ continuous: false, language: "en-US" });
  };

  const stopListening = () => {
    SpeechRecognition.stopListening();
  };

  const askTutor = async (text) => {
    if (!text || text.trim() === "") return;
    console.log("üé§ Sending voice text to server:", text);
    setLoading(true);

    try {
      // ‚úÖ route fixed: matches backend (port 5000 + /api/tutor)
      const res = await fetch("http://localhost:5000/api/tutor", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ text }),
      });

      const data = await res.json();
      console.log("‚úÖ Server response:", data);

      setResponse(data.reply || "");
      setAudioUrl(data.audio || "");
      setVideoUrl(data.video || "");
    } catch (err) {
      console.error("‚ùå Tutor Error:", err);
      setResponse("Error contacting tutor");
      setAudioUrl("");
      setVideoUrl("");
    }

    setLoading(false);
  };

  useEffect(() => {
    if (transcript && transcript.trim() !== "") {
      console.log("üéô Transcript captured:", transcript);
      askTutor(transcript);
    }
  }, [transcript]);

  if (!isSupported) {
    return <p>Your browser does not support speech recognition.</p>;
  }

  return (
    <div style={{ padding: "20px", fontFamily: "sans-serif" }}>
      <h1>üé§ AI Voice Tutor</h1>
      <p>Ask any question and let the tutor answer you!</p>

      <button onClick={startListening} style={{ marginRight: "10px" }}>Start Listening</button>
      <button onClick={stopListening}>Stop</button>

      <p>Listening: {listening ? "Yes ‚úÖ" : "No ‚ùå"}</p>
      <p><strong>Transcript:</strong> "{transcript}"</p>

      {loading && <p>Loading tutor response...</p>}

      {response && (
        <div style={{ marginTop: "20px" }}>
          <p><strong>You asked:</strong> "{transcript}"</p>
          <p><strong>Tutor says:</strong> "{response}"</p>

          {audioUrl && (
            <audio controls autoPlay style={{ display: "block", marginTop: "10px" }}>
              <source src={audioUrl} type="audio/mp3" />
            </audio>
          )}

          {videoUrl && (
            <div style={{ marginTop: "10px" }}>
              <video src={videoUrl} controls width="400" />
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
