import React, { useState, useEffect, useRef } from "react";
import SpeechRecognition, { useSpeechRecognition } from "react-speech-recognition";

function App() {
  const [messages, setMessages] = useState([]);
  const [isSupported, setIsSupported] = useState(true);
  const [darkMode, setDarkMode] = useState(false);
  const [typing, setTyping] = useState(false);
  const [voiceIndex, setVoiceIndex] = useState(0);
  const { transcript, listening, resetTranscript } = useSpeechRecognition();
  const chatEndRef = useRef(null);

  useEffect(() => {
    if (!SpeechRecognition.browserSupportsSpeechRecognition()) {
      setIsSupported(false);
    }
  }, []);

  useEffect(() => {
    if (chatEndRef.current) chatEndRef.current.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const startListening = () => {
    resetTranscript();
    SpeechRecognition.startListening({ continuous: true, language: "en-US" });
  };

  const stopListening = () => SpeechRecognition.stopListening();

  const toggleDarkMode = () => setDarkMode(!darkMode);

  const getVoices = () => window.speechSynthesis.getVoices();

  // Demo tutor reply logic
  const generateTutorReply = (text) => {
    const lower = text.toLowerCase();

    // Gmail & email help
    const gmailKeywords = ["gmail", "email", "login", "password", "account"];
    if (gmailKeywords.some(k => lower.includes(k))) {
      return "To fix your Gmail account, first check your password and recovery options. If you forgot it, use 'Forgot Password'.";
    }

    // Math help
    const mathKeywords = ["math", "calculate", "problem", "equation", "solve"];
    if (mathKeywords.some(k => lower.includes(k))) {
      return "Let‚Äôs solve your math problem step by step. Can you tell me the equation?";
    }

    // React / JS
    const reactKeywords = ["react", "component", "jsx"];
    if (reactKeywords.some(k => lower.includes(k))) {
      return "React is a frontend library used to build interactive UI. Components are the building blocks!";
    }

    const jsKeywords = ["javascript", "js", "variable", "function"];
    if (jsKeywords.some(k => lower.includes(k))) {
      return "JavaScript is a versatile language for web development. Functions, variables, and events are its core.";
    }

    // Greetings
    if (lower.includes("hello") || lower.includes("hi")) return "Hello! How can I help you today?";

    // General fallback
    return "Interesting! Can you explain more so I can help you better?";
  };

  const askTutor = (text) => {
    if (!text.trim()) return;

    setMessages((prev) => [...prev, { sender: "user", text }]);

    setTyping(true);
    setTimeout(() => {
      const reply = generateTutorReply(text);
      setTyping(false);

      setMessages((prev) => [...prev, { sender: "tutor", text: reply }]);

      const utterance = new SpeechSynthesisUtterance(reply);
      const voices = getVoices();
      if (voices.length > 0) utterance.voice = voices[voiceIndex % voices.length];
      utterance.lang = "en-US";
      window.speechSynthesis.speak(utterance);
    }, 1000);
  };

  useEffect(() => {
    if (transcript && transcript.trim() !== "") {
      const timeout = setTimeout(() => {
        askTutor(transcript);
        resetTranscript();
      }, 1000);
      return () => clearTimeout(timeout);
    }
  }, [transcript]);

  if (!isSupported) return <p>Your browser does not support speech recognition.</p>;

  return (
    <div style={{
      padding: "20px",
      fontFamily: "sans-serif",
      maxWidth: "600px",
      margin: "0 auto",
      background: darkMode ? "#121212" : "#f0f0f0",
      color: darkMode ? "#f0f0f0" : "#121212",
      minHeight: "100vh"
    }}>
      <h1>üé§ Demo Voice Tutor</h1>
      <p>Talk to your tutor and get smart mock responses! üí¨</p>

      <div style={{ marginBottom: "10px" }}>
        <button onClick={startListening} style={{ marginRight: "10px", padding: "0.5rem 1rem" }}>Start Listening</button>
        <button onClick={stopListening} style={{ padding: "0.5rem 1rem", marginRight: "10px" }}>Stop</button>
        <button onClick={toggleDarkMode} style={{ padding: "0.5rem 1rem" }}>
          {darkMode ? "Light Mode" : "Dark Mode"}
        </button>
        <button onClick={() => setVoiceIndex((prev) => prev + 1)}
                style={{ padding: "0.5rem 1rem", marginLeft: "10px" }}>
          Change Voice
        </button>
      </div>

      <p>Listening: {listening ? "Yes ‚úÖ" : "No ‚ùå"}</p>
      <p><strong>Current transcript:</strong> "{transcript}"</p>

      <div style={{
        border: "1px solid #ccc",
        borderRadius: "8px",
        padding: "10px",
        marginTop: "20px",
        height: "400px",
        overflowY: "auto",
        background: darkMode ? "#1e1e1e" : "#fff",
      }}>
        {messages.map((msg, idx) => (
          <div key={idx} style={{
            display: "flex",
            justifyContent: msg.sender === "user" ? "flex-end" : "flex-start",
            marginBottom: "10px",
          }}>
            <div style={{
              background: msg.sender === "user" ? "#4caf50" : "#2196f3",
              color: "white",
              padding: "8px 12px",
              borderRadius: "16px",
              maxWidth: "70%",
            }}>
              {msg.text}
            </div>
          </div>
        ))}
        {typing && <div style={{ fontStyle: "italic", marginBottom: "10px" }}>Tutor is typing...</div>}
        <div ref={chatEndRef}></div>
      </div>
    </div>
  );
}

export default App;
