export default function smartTutorAI(text) {
  if (!text || text.trim().length === 0) {
    return "Ami bujhte parini—please arektu clear kore bolben?";
  }

  const msg = text.toLowerCase().trim();

  // ---- 1. Greetings ----
  if (
    msg.includes("hello") ||
    msg.includes("hi") ||
    msg.includes("hey") ||
    msg.includes("salam") ||
    msg.includes("tutor")
  ) {
    return "Hello! Ami apnar Smart Tutor. Ki niye help chai?";
  }

  // ---- 2. WordPress ----
  if (
    msg.includes("wordpress") ||
    msg.includes("elementor") ||
    msg.includes("plugin") ||
    msg.includes("theme") ||
    msg.includes("kadence")
  ) {
    if (msg.includes("error")) {
      return "WordPress e kon error ta asche? Full error message dile ami fix kore dibo.";
    }
    if (msg.includes("website") || msg.includes("site")) {
      return "WordPress website banate chaile ami full guide dite pari. Ki type site—blog, news, ecommerce naki business?";
    }
    return "WordPress niye kaj korte chacchen? Theme, plugin, speed optimization naki security?";
  }

  // ---- 3. Website ----
  if (msg.includes("website") || msg.includes("site banate") || msg.includes("build a site")) {
    return "Website banate chaile apnake help korbo. Ki type website lagbe—WordPress, React, business, blog, portfolio?";
  }

  // ---- 4. React / Frontend ----
  if (
    msg.includes("react") ||
    msg.includes("frontend") ||
    msg.includes("component") ||
    msg.includes("state") ||
    msg.includes("props")
  ) {
    if (msg.includes("error")) {
      return "React e kon error ta paichen? Error details dile ami fix kore dibo.";
    }
    return "React niye help chai? Component, state, API call—kon topic e problem hocche?";
  }

  // ---- 5. API / Backend ----
  if (
    msg.includes("api") ||
    msg.includes("backend") ||
