// ---------------- AI LOGIC: SMART TOPIC DETECTION ----------------
function smartTutorAI(text) {
  if (!text) return "I did not understand. Please say again.";

  text = text.toLowerCase();

  // GREETINGS
  if (text.includes("hello") || text.includes("hi")) {
    return "Hello! I am your smart tutor. How can I guide you today?";
  }

  // WEBSITE / WORDPRESS
  if (text.includes("wordpress") || text.includes("website") || text.includes("site")) {
    return "You want to build a website? WordPress is a great choice! Tell me — business website, blog, or ecommerce?";
  }

  // REACT
  if (text.includes("react")) {
    return "React helps you build fast single-page apps. You can start with components, props, and useState. Want an example?";
  }

  // PROJECT
  if (text.includes("project")) {
    return "Great! For a new project: 1) Define purpose, 2) Select tools, 3) Build UI, 4) Add backend. What type of project do you want?";
  }

  // ENGLISH / LEARNING
  if (text.includes("english") || text.includes("toefl")) {
    return "TOEFL practice: Speak clear sentences. I can help you practice. Ask me anything in English!";
  }

  // TV or Random Words
  if (text.includes("tv")) {
    return "Do you want recommendations? Or information related to smart TVs?";
  }

  // DEFAULT SAFE REPLY
  return "I understand you want help — could you explain a little more? I’m listening.";
}

export default smartTutorAI;
