import React, { useEffect, useState } from "react";
import smartTutorAI from "./smartTutorAI";


function VideoTutor({ text }) {
  const [reply, setReply] = useState("");
  const [tutorVideo, setTutorVideo] = useState(null);

  useEffect(() => {
    if (!text || text.trim() === "") return;

    // ðŸ§  Generate AI Reply
    const generatedReply = smartTutorAI(text);
    setReply(generatedReply);

    // ðŸŽ¥ Always show same tutor video (mock tutor)
    // Put your tutor video file in: public/tutor.mp4
    const videoPath = "/tutor.mp4";
    setTutorVideo(videoPath);

  }, [text]);

  return (
    <div style={{ marginTop: "20px", padding: "15px", border: "1px solid #ddd", borderRadius: "10px" }}>
      <h3>ðŸŽ§ Tutor Response</h3>

      <p><strong>You said:</strong> {text}</p>
      <p><strong>Tutor:</strong> {reply}</p>

      {tutorVideo && (
        <video
          src={tutorVideo}
          width="380"
          autoPlay
          loop
          style={{ borderRadius: "12px", marginTop: "10px" }}
        />
      )}
    </div>
  );
}

export default VideoTutor;
