import React, { useState } from "react";

const synth = window.speechSynthesis || null;

function App() {
  const [listening, setListening] = useState(false);
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [guide, setGuide] = useState([]);

  // Safety check for voice recognition
  let recognition = null;
  if ("webkitSpeechRecognition" in window) {
    recognition = new window.webkitSpeechRecognition();
    recognition.continuous = false;
    recognition.interimResults = false;
    recognition.lang = "en-US";

    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setQuestion(transcript);
      askTutor(transcript);
    };
  }

  const startListening = () => {
    if (!recognition) {
      alert("Your browser does not support voice input.");
      return;
    }
    setListening(true);
    recognition.start();
  };

  const stopListening = () => {
    if (!recognition) return;
    setListening(false);
    recognition.stop();
  };

  // Mock / rule-based response
  const askTutor = async (q) => {
    let answerText = "React is a frontend library used to build interactive UI.";
    let guideSteps = [
      "Step 1: Install Node.js and npm",
      "Step 2: Run: npx create-react-app my-app",
      "Step 3: cd my-app",
      "Step 4: npm start",
      "Step 5: Add components and pages"
    ];

    if (q.toLowerCase().includes("website")) {
      answerText = "You can create a website using HTML, CSS, React.";
      guideSteps = [
        "Step 1: Choose tech stack",
        "Step 2: Create project folder",
        "Step 3: Add pages & components",
        "Step 4: Test & Deploy"
      ];
    }

    setAnswer(answerText);
    setGuide(guideSteps);
    speakAnswer(answerText, guideSteps);
  };

  const speakAnswer = (ans, guideSteps) => {
    if (!synth) return;
    const utterance = new SpeechSynthesisUtterance(ans + " " + guideSteps.join(". "));
    synth.speak(utterance);
  };

  return (
    <div style={{ padding: 30, fontFamily: "Arial" }}>
      <h1>üéôÔ∏è Rule-Based Voice Tutor (Mockup)</h1>

      <button onClick={listening ? stopListening : startListening}>
        {listening ? "Stop Listening" : "Start Talking"}
      </button>

      {question && <p><b>You asked:</b> {question}</p>}
      {answer && <p><b>Answer:</b> {answer}</p>}
      {guide.length > 0 && (
        <div>
          <b>Step-by-Step Guide:</b>
          <ol>
            {guide.map((g, idx) => <li key={idx}>{g}</li>)}
          </ol>
        </div>
      )}

      {/* Tutor Video Placeholder */}
      <div style={{
        marginTop: 20,
        width: 400,
        height: 300,
        border: "2px solid #ccc",
        textAlign: "center",
        lineHeight: "300px",
        fontWeight: "bold"
      }}>
        Tutor Video Here
      </div>
    </div>
  );
}

export default App;
