export default function smartTutorAI(text) {
  if (!text || text.trim().length === 0) {
    return "Ami bujhte parini—please arektu clear kore bolben?";
  }

  const msg = text.toLowerCase().trim();

  // Greetings
  if (msg.includes("hello") || msg.includes("hi") || msg.includes("hey") || msg.includes("tutor")) {
    return "Hello! Ami apnar Smart Tutor. Ki niye help chai?";
  }

  // English conversation
  if (msg.includes("english") || msg.includes("talk in english") || msg.includes("speak english")) {
    return "Sure! I can talk in English. Ask any question about your project or website.";
  }

  // WordPress
  if (msg.includes("wordpress") || msg.includes("elementor") || msg.includes("plugin") || msg.includes("theme")) {
    if (msg.includes("error")) return "WordPress e kon error ta asche? Full error message dile ami fix kore dibo.";
    return "WordPress website banate chaile ami full guide dite pari. Ki type site—blog, news, ecommerce naki business?";
  }

  // Website
  if (msg.includes("website") || msg.includes("site banate")) {
    return "Website banate chaile apnake help korbo. Ki type website lagbe—WordPress, React, business, blog, portfolio?";
  }

  // React / Frontend
  if (msg.includes("react") || msg.includes("frontend") || msg.includes("component")) {
    if (msg.includes("error")) return "React e kon error ta paichen? Error details dile ami fix kore dibo.";
    return "React niye help chai? Component, state, API call—kon topic e problem hocche?";
  }

  // Backend / API
  if (msg.includes("api") || msg.includes("backend") || msg.includes("server")) {
    return "API/backend niye help chai? API call, server error, CORS, token issue—ki hocche?";
  }

  // AI / Chatbot / Voice
  if (msg.includes("voice") || msg.includes("chatbot") || msg.includes("ai")) {
    return "AI/Voice tutor setup korte chai? Speech-to-text, text-to-speech, API integration — sob guide korte parbo.";
  }

  // Automation
  if (msg.includes("automation") || msg.includes("workflow") || msg.includes("zapier") || msg.includes("smythos")) {
    return "Automation workflow korte chai? Apnar requirement bolun — ami pure setup pathabo.";
  }

  // Fix / Error
  if (msg.includes("error") || msg.includes("not working") || msg.includes("problem")) {
    return "Error ta fully likhe din — ami step-by-step fix kore dibo.";
  }

  // Generic intent
  if (msg.startsWith("i want") || msg.startsWith("i need") || msg.includes("chai") || msg.includes("lagbe")) {
    return "Thik ache! Apni ki korte chan arektu specific kore bolun — ami full guide dibo.";
  }

  // Fallback
  return "Ami try korchi bujhte—please arektu clear kore bolun ki korte chacchen?";
}
